// 1. On r√©cup√®re l'objet principal
const input = $input.first().json;

// 2. On parse la cha√Æne de caract√®res JSON contenue dans body.keywords
const keywordsArray = input.body.keywords;

// 3. On g√©n√®re le brief textuel pour le prompt
const seoBrief = keywordsArray.map(item => {
  let statusBadges = [];
  if (item.status.promising) statusBadges.push("Promising üíé");
  if (item.status.trending) statusBadges.push("Trending üî•");
  if (item.status.evergreen) statusBadges.push("Evergreen üå≤");

  const badgeText = statusBadges.length > 0 ? statusBadges.join(" + ") : "Standard";

  return `- Keyword: "${item.keyword}" | Status: ${badgeText} | Volume: ${item.avg_volume} | Comp: ${item.competition}`;
}).join('\n');

// 4. On retourne un objet complet pr√™t pour le n≈ìud LLM
return {
  mockup_url: input.mockup_url,
  product_type: input.ProductType,
  slogan: input.Slogan,
  design_description: input.Description,
  niche_info: `${input.Theme} > ${input.Niche} > ${input.SubNiche}`,
  seo_brief: seoBrief
};

// On r√©cup√®re le contenu markdown du n≈ìud pr√©c√©dent
const markdown = $input.first().json.data.markdown;

if (!markdown) return items;

let cleanedText = markdown
  // 1. Supprimer toutes les images ![](...)
  .replace(/!\[.*?\]\(.*?\)/g, '')

  // 2. Supprimer les liens de navigation inutiles (Favoris, Contact, Sign-in, etc.)
  .replace(/\[.*?\]\(https:\/\/www\.etsy\.com\/(signin|messages|people|conversations|cart|help).*?\)/g, '')

  // 3. Supprimer les IDs de tracking et nonces (souvent des longues cha√Ænes alphanum√©riques)
  .replace(/[a-zA-Z0-0]{30,}/g, '')

  // 4. Supprimer les mentions de prix et "Sale Price" qui n'aident pas pour l'identit√© de marque
  .replace(/(Sale Price|Original Price|off|\$\d+\.\d+)/gi, '')

  // 5. Supprimer les sections de cookies et de param√®tres de confidentialit√© (souvent √† la fin)
  .split("Your Etsy Privacy Settings")[0]
  .split("Privacy Settings")[0]

  // 6. Nettoyer les espaces vides multiples et les sauts de ligne excessifs
  .replace(/\n\s*\n/g, '\n\n')
  .trim();

// On renvoie le texte nettoy√©
return [{
  json: {
    cleaned_content: cleanedText
  }
}];

const results = $input.first().json.tasks[0].result;
const tag_count = $('Edit Fields').first().json.Tag_count

let processed = results
  .filter(item => item.search_volume !== null && item.monthly_searches !== null)
  .map(item => {
    const avgVolume = item.search_volume;
    const comp = item.competition || 0.5;

    // 1. Historique des volumes (du plus ancien au plus r√©cent)
    const monthlyVolumes = item.monthly_searches
      .map(m => m.search_volume)
      .reverse();

    // 2. Calculs des tendances et statuts
    const last3MonthsAvg = (monthlyVolumes[11] + monthlyVolumes[10] + monthlyVolumes[9]) / 3;
    const first3MonthsAvg = (monthlyVolumes[0] + monthlyVolumes[1] + monthlyVolumes[2]) / 3;

    const isTrending = last3MonthsAvg > (first3MonthsAvg * 1.3);
    const isEvergreen = monthlyVolumes.every(v => v > (avgVolume * 0.3));
    const isPromising = avgVolume > 300 && comp < 0.3;

    // 3. Score d'opportunit√© pond√©r√©
    let finalScore = (avgVolume * (1 - comp + 0.1)) / 10;
    if (isTrending) finalScore *= 1.5;   // Boost tendance
    if (isPromising) finalScore *= 1.3;  // Boost niche facile
    if (item.keyword.split(' ').length >= 3) finalScore *= 1.2; // Boost longue tra√Æne

    const opportunityScore = Math.round(finalScore);

    // FORMAT STRICTEMENT DEMAND√â
    return {
      keyword: item.keyword,
      avg_volume: avgVolume,
      competition: comp,
      opportunity_score: opportunityScore,
      volumes_history: monthlyVolumes,
      status: {
        trending: isTrending,
        evergreen: isEvergreen,
        promising: isPromising
      }
    };
  });

// 4. S√©lection des 13 meilleurs avec diversit√© s√©mantique
const selectedTags = [];
const wordTracker = {};

// On trie par le nouveau score d'opportunit√©
processed.sort((a, b) => b.opportunity_score - a.opportunity_score);

for (const item of processed) {
  if (selectedTags.length >= tag_count) break;

  const firstWord = item.keyword.split(' ')[0].toLowerCase();
  wordTracker[firstWord] = (wordTracker[firstWord] || 0) + 1;

  // R√®gle de diversit√© : maximum 3 tags commen√ßant par le m√™me mot
  if (wordTracker[firstWord] <= 3) {
    selectedTags.push(item);
  }
}

return selectedTags;

// 1. R√©cup√©ration des donn√©es d'entr√©e
const results = $input.first().json.tasks[0].result;
const editFields = $('Edit Fields').first().json;
const tag_count = editFields.Tag_count || 13;
const productType = (editFields.ProductType || 'mug').toLowerCase();

let processed = results
  .filter(item => item.search_volume !== null && item.monthly_searches !== null)
  .map(item => {
    const avgVolume = item.search_volume;
    const comp = item.competition || 0.5;
    const kw = item.keyword.toLowerCase();

    // Historique des volumes (du plus ancien au plus r√©cent)
    const monthlyVolumes = item.monthly_searches.map(m => m.search_volume).reverse();

    // Calculs des tendances et statuts
    const last3MonthsAvg = (monthlyVolumes[11] + monthlyVolumes[10] + monthlyVolumes[9]) / 3;
    const first3MonthsAvg = (monthlyVolumes[0] + monthlyVolumes[1] + monthlyVolumes[2]) / 3;

    const isTrending = last3MonthsAvg > (first3MonthsAvg * 1.3);
    const isEvergreen = monthlyVolumes.every(v => v > (avgVolume * 0.2));
    const isPromising = avgVolume > 200 && comp < 0.4;

    // --- FORMULE DU SCORE D'OPPORTUNIT√â ---
    // Utilisation de la racine carr√©e pour √©quilibrer le volume et la niche
    let finalScore = (Math.sqrt(avgVolume) * (1 - comp + 0.2));

    // D√âFINITION DES GROUPES D'INTENTION (Logique Cumulative)
    const highIntentGroups = {
        product: [productType, 'cup', 'drinkware', 'mug'], 
        custom: ['custom', 'personalized', 'name', 'initial'], 
        roles: ['mom', 'dad', 'teacher', 'nurse', 'coworker', 'boss', 'friend', 'grandma', 'colleague'], 
        occasions: ['birthday', 'wedding', 'christmas', 'graduation', 'retirement', 'anniversary', 'santa'], 
        gift: ['gift', 'present', 'token', 'memento', 'gag'] 
    };

    let boostCount = 0;

    // On applique un boost de 40% pour chaque cat√©gorie d'intention trouv√©e
    Object.values(highIntentGroups).forEach(group => {
        if (group.some(word => kw.includes(word))) {
            finalScore *= 1.4; 
            boostCount++;
        }
    });

    // Bonus sp√©cial "Combo" : si le tag touche 3 cat√©gories ou plus (ex: "Custom Teacher Gift")
    if (boostCount >= 3) finalScore *= 1.5;

    // Boost Longue Tra√Æne (Pr√©cision de recherche)
    const wordCount = item.keyword.split(' ').length;
    if (wordCount >= 3) finalScore *= 1.3;

    // Boosts de Performance (Dataforseo trends)
    if (isTrending) finalScore *= 1.3;
    if (isPromising) finalScore *= 1.2;

    return {
      keyword: item.keyword,
      avg_volume: avgVolume,
      competition: comp,
      opportunity_score: Math.round(finalScore),
      volumes_history: monthlyVolumes,
      status: {
        trending: isTrending,
        evergreen: isEvergreen,
        promising: isPromising
      }
    };
  });

// 2. S√©lection avec Diversit√© S√©mantique (Concept Tracker)
const selectedTags = [];
const conceptTracker = {};

// Tri par score d'opportunit√© d√©croissant
processed.sort((a, b) => b.opportunity_score - a.opportunity_score);

for (const item of processed) {
  if (selectedTags.length >= tag_count) break;

  const words = item.keyword.toLowerCase().split(' ');
  // On identifie le concept par les deux premiers mots
  const concept = words.slice(0, 2).join(' ');

  conceptTracker[concept] = (conceptTracker[concept] || 0) + 1;

  // R√®gle de diversit√© : maximum 2 tags par concept identique
  if (conceptTracker[concept] <= 2) {
    selectedTags.push(item);
  }
}

return selectedTags;
