// 1. R√©cup√©ration des donn√©es depuis le n≈ìud pr√©c√©dent
// On acc√®de au premier item entrant, puis √† la cl√© "result" qui contient le tableau
const results = $input.first().json.result;

// R√©cup√©ration des param√®tres utilisateur (avec valeurs par d√©faut s√©curis√©es)
const editFields = $("Edit Fields").first().json || {};
const tag_count = editFields.Tag_count || 13;

let processed = results.map(item => {
    // --- Initialisation des variables avec valeurs par d√©faut si null ---
    const avgVolume = item.search_volume || 0;
    const comp = (item.competition !== null && item.competition !== undefined) ? item.competition : 0.5;
    const kw = (item.keyword || "").toLowerCase();

    const txScore = item.transactional_score || 5; 
    const intentLabel = item.intent_label || 'Tag'; 
    const nicheScore = item.niche_score || 5; 
    const relevance_label = item.relevance_label || 'Broad';

    // --- Gestion s√©curis√©e de l'historique des volumes ---
    // Si monthly_searches est null ou n'est pas un tableau, on utilise un tableau de 12 z√©ros
    let monthlyVolumes = [];
    if (item.monthly_searches && Array.isArray(item.monthly_searches)) {
        monthlyVolumes = item.monthly_searches.map(m => m.search_volume || 0).reverse();
    } else {
        monthlyVolumes = new Array(12).fill(0);
    }

    // --- Calculs des tendances (bas√©s sur les 12 mois) ---
    // S√©curit√© : on s'assure d'avoir assez de donn√©es, sinon moyennes √† 0
    const last3MonthsAvg = monthlyVolumes.length >= 12 
        ? (monthlyVolumes[11] + monthlyVolumes[10] + monthlyVolumes[9]) / 3 
        : 0;
    const first3MonthsAvg = monthlyVolumes.length >= 3 
        ? (monthlyVolumes[0] + monthlyVolumes[1] + monthlyVolumes[2]) / 3 
        : 0;

    const isTrending = last3MonthsAvg > (first3MonthsAvg * 1.3);
    const isEvergreen = monthlyVolumes.length > 0 && monthlyVolumes.every(v => v > (avgVolume * 0.2));
    const isPromising = avgVolume > 200 && comp < 0.4;

    // --- √âTAPE 1 : FORMULE DE BASE (OPPORTUNIT√â) ---
    // Racine carr√©e pour lisser l'impact des √©normes volumes
    let finalScore = (Math.sqrt(avgVolume) * (1 - comp + 0.2));

    // --- √âTAPE 2 : BOOST TRANSACTIONNEL ---
    finalScore *= (1 + (txScore / 10));

    // --- √âTAPE 3 : LOGIQUE DE NICHE (PENNYSEO STRATEGY) ---
    ////--if (nicheScore >= 7) {
     ////--    finalScore *= (1 + (nicheScore / 10)); // Boost pour pertinence √©lev√©e
     ////--} else if (nicheScore <= 2) {
     ////--    finalScore *= 0.5; // P√©nalit√© pour hors-sujet
     ////--}

    // --- NOUVELLE LOGIQUE DE NICHE DURCIE ---
    if (nicheScore >= 8) {
        // BOOST AGRESSIF (Sniper Turbo)
        // On passe de +100% √† +300% pour les scores 8-10
        finalScore *= 4.0; 
    } 
    else if (nicheScore <= 4) {
        // P√âNALIT√â √âLIMINATOIRE (Le Mur)
        // Tout ce qui est sous 5 est consid√©r√© comme du "bruit"
        // On passe de 0.5x √† 0.1x
        finalScore *= 0.1;
    }
    else {
        // ZONE GRISE (Scores 5, 6, 7)
        // On ne booste pas, on laisse le volume d√©cider mais sans avantage
        finalScore *= 1.0;
    }



    // --- √âTAPE 4 : BOOSTS STRAT√âGIQUES ---
    if (intentLabel === 'Title') finalScore *= 1.25;

    const wordCount = kw.split(' ').filter(w => w.length > 0).length;
    if (wordCount >= 3) finalScore *= 1.2;

    if (isTrending) finalScore *= 1.3;
    if (isPromising) finalScore *= 1.2;

    return {
        ...item,
        opportunity_score: Math.round(finalScore),
        status: {
            trending: isTrending,
            evergreen: isEvergreen,
            promising: isPromising
        }
    };
});

// 2. S√©lection et Diversit√© (Concept Tracker)
const selectedTags = [];
const conceptTracker = {};

// Tri par score d√©croissant
processed.sort((a, b) => b.opportunity_score - a.opportunity_score);

for (const item of processed) {
    if (selectedTags.length >= tag_count) break;

    const words = item.keyword.toLowerCase().split(' ');
    const concept = words[0]; // On utilise le premier mot comme racine du concept

    conceptTracker[concept] = (conceptTracker[concept] || 0) + 1;

    // Limite : Max 2 tags commen√ßant par le m√™me mot pour maximiser la diversit√© s√©mantique
    if (conceptTracker[concept] <= 2) {
        selectedTags.push(item);
    }
}

return selectedTags;

