// 1. R√©cup√©ration s√©curis√©e des 13 tags depuis "Edit Fields"
// On utilise .first().json pour cibler l'objet racine du n≈ìud
const data = $('Edit Fields').first().json;
const allTags = data.body.keywords || [];

// 2. Tri de la liste par opportunity_score d√©croissant (du plus haut au plus bas)
// On utilise (b - a) pour avoir les meilleurs en premier
const sortedTags = [...allTags].sort((a, b) => {
  return (b.opportunity_score || 0) - (a.opportunity_score || 0);
});

// 3. S√©lection des 3 meilleurs
const top3 = sortedTags.slice(0, 3).map(t => ({ 
  ...t, 
  target_type: 'top' 
}));

// 4. S√©lection des 2 moins bons (les deux derniers de la liste tri√©e)
const bottom2 = sortedTags.slice(-2).map(t => ({ 
  ...t, 
  target_type: 'bottom' 
}));

// 5. Retour des donn√©es regroup√©es
return {
  targets: [...top3, ...bottom2]
};

// 1. On r√©cup√®re l'objet principal
const input = $input.first().json;

// 2. On parse la cha√Æne de caract√®res JSON contenue dans body.keywords
const keywordsArray = input.body.keywords;

// 3. On g√©n√®re le brief textuel pour le prompt
const seoBrief = keywordsArray.map(item => {
  let statusBadges = [];
  if (item.status.promising) statusBadges.push("Promising üíé");
  if (item.status.trending) statusBadges.push("Trending üî•");
  if (item.status.evergreen) statusBadges.push("Evergreen üå≤");

  const badgeText = statusBadges.length > 0 ? statusBadges.join(" + ") : "Standard";

  return `- Keyword: "${item.keyword}" | Status: ${badgeText} | Volume: ${item.avg_volume} | Comp: ${item.competition}`;
}).join('\n');

// 4. On retourne un objet complet pr√™t pour le n≈ìud LLM
return {
  mockup_url: input.mockup_url,
  product_type: input.ProductType,
  slogan: input.Slogan,
  design_description: input.Description,
  niche_info: `${input.Theme} > ${input.Niche} > ${input.SubNiche}`,
  seo_brief: seoBrief
};

// Extraction et regroupement pour PennySEO
const keywords = items[0].json.body.keywords;

const scoredKeywords = keywords.map(k => {
  let score = k.opportunity_score || 0;

  // 1. Priorit√© aux recommandations (is_top)
  if (k.is_top === true) score *= 2.0;
  if (k.is_top === false) score *= 0.5;

  // 2. Bonus de v√©locit√©
  if (k.status.promising) score *= 1.5;
  else if (k.status.trending) score *= 1.2;

  // 3. Bonus Long-tail (3 mots ou plus)
  const wordCount = k.keyword.split(' ').length;
  if (wordCount >= 3) score *= 1.3;

  return { ...k, final_rank_score: score };
});

// 4. Tri par score pond√©r√©
let sorted = scoredKeywords.sort((a, b) => b.final_rank_score - a.final_rank_score);

// 5. Garantie Evergreen
let top5 = sorted.slice(0, 5);
const hasEvergreen = top5.some(k => k.status.evergreen);

if (!hasEvergreen) {
    const bestEvergreen = sorted.slice(5).find(k => k.status.evergreen);
    if (bestEvergreen) {
        top5[4] = bestEvergreen;
    }
}

// 6. REGROUPEMENT : On retourne un seul objet contenant la liste
return [{
  json: {
    selected_keywords: top5,
    keyword_list_string: top5.map(k => k.keyword).join(', '), // Pratique pour les prompts
    count: top5.length
  }
}];

// 1. R√©cup√©ration des donn√©es d'entr√©e
const results = $input.first().json.tasks[0].result;
const editFields = $('Edit Fields').first().json;
const tag_count = editFields.Tag_count || 13;
const productType = (editFields.ProductType || 'mug').toLowerCase();

let processed = results
  .filter(item => item.search_volume !== null && item.monthly_searches !== null)
  .map(item => {
    const avgVolume = item.search_volume;
    const comp = item.competition || 0.5;
    const kw = item.keyword.toLowerCase();

    const monthlyVolumes = item.monthly_searches.map(m => m.search_volume).reverse();

    const last3MonthsAvg = (monthlyVolumes[11] + monthlyVolumes[10] + monthlyVolumes[9]) / 3;
    const first3MonthsAvg = (monthlyVolumes[0] + monthlyVolumes[1] + monthlyVolumes[2]) / 3;

    const isTrending = last3MonthsAvg > (first3MonthsAvg * 1.3);
    const isEvergreen = monthlyVolumes.every(v => v > (avgVolume * 0.2));
    const isPromising = avgVolume > 200 && comp < 0.4;

    // --- FORMULE DU SCORE MODIFI√âE ---
    // On garde la logique de base mais on ajoute un plancher de viabilit√©
    let finalScore = (Math.sqrt(avgVolume) * (1 - comp + 0.2));

    // AJUSTEMENT DE VIABILIT√â (Anti-volume mort)
    if (avgVolume < 50) finalScore *= 0.3;      // P√©nalit√© lourde pour les volumes quasi nuls
    else if (avgVolume > 1000) finalScore *= 1.5; // Bonus de port√©e pour assurer la visibilit√©

    const highIntentGroups = {
        product: [productType, 'cup', 'drinkware', 'mug', 'tee', 'shirt', 'apparel'], 
        custom: ['custom', 'personalized', 'name', 'initial'], 
        roles: ['mom', 'dad', 'teacher', 'nurse', 'coworker', 'friend', 'autistic', 'neurodivergent'], 
        occasions: ['birthday', 'wedding', 'christmas', 'graduation', 'anniversary'], 
        gift: ['gift', 'present', 'token', 'memento', 'gag'] 
    };

    let boostCount = 0;
    Object.values(highIntentGroups).forEach(group => {
        if (group.some(word => kw.includes(word))) {
            finalScore *= 1.4; 
            boostCount++;
        }
    });

    if (boostCount >= 3) finalScore *= 1.5;
    const wordCount = item.keyword.split(' ').length;
    if (wordCount >= 3) finalScore *= 1.3;
    if (isTrending) finalScore *= 1.3;
    if (isPromising) finalScore *= 1.2;

    return {
      keyword: item.keyword,
      avg_volume: avgVolume,
      competition: comp,
      opportunity_score: Math.round(finalScore),
      volumes_history: monthlyVolumes,
      status: {
        trending: isTrending,
        evergreen: isEvergreen,
        promising: isPromising
      }
    };
  });

// 2. S√©lection avec Diversit√© S√©mantique
const selectedTags = [];
const conceptTracker = {};

// On s'assure d'avoir au moins un "Evergreen" solide dans la s√©lection finale
processed.sort((a, b) => b.opportunity_score - a.opportunity_score);

for (const item of processed) {
  if (selectedTags.length >= tag_count) break;

  const words = item.keyword.toLowerCase().split(' ');
  const concept = words.slice(0, 2).join(' ');

  conceptTracker[concept] = (conceptTracker[concept] || 0) + 1;

  // Diversit√© : Max 2 tags par concept
  if (conceptTracker[concept] <= 2) {
    selectedTags.push(item);
  }
}

// Validation finale : Si aucun Evergreen n'est pr√©sent, on force le meilleur Evergreen dispo
if (!selectedTags.some(t => t.status.evergreen) && processed.length > 0) {
    const bestEvergreen = processed.find(t => t.status.evergreen);
    if (bestEvergreen) selectedTags[selectedTags.length - 1] = bestEvergreen;
}

return selectedTags;

// On r√©cup√®re les 13 tags du n≈ìud pr√©c√©dent
const tags = $input.all().map(item => item.json);

// S√©lection des 3 meilleurs
const top3 = tags.slice(0, 3).map(t => ({ ...t, target_type: 'top' }));

// S√©lection des 2 moins bons
const bottom2 = tags.slice(-2).map(t => ({ ...t, target_type: 'bottom' }));

return {
  targets: [...top3, ...bottom2]
};

// 1. R√©cup√©ration des deux sources
const editFieldsNode = $('Edit Fields').first().json;
const sniperNode = $('sniper_keywords').first().json;

// Extraction s√©curis√©e
const existingKeywords = editFieldsNode?.body?.keywords || [];
// CORRECTION : On cible sp√©cifiquement la cl√© 'targets' identifi√©e dans ton JSON
const sniperKeywords = sniperNode?.targets || [];

// 2. Trier les 13 existants par score croissant pour identifier les plus faibles
let rankedExisting = [...existingKeywords].sort((a, b) => 
  (a.opportunity_score || 0) - (b.opportunity_score || 0)
);

// Identification des 5 plus faibles (en minuscules pour comparaison)
const keywordsToReplace = rankedExisting.slice(0, 5).map(k => k.keyword.toLowerCase());

// 3. Filtrer pour garder les 8 plus forts (is_sniper_seo: false)
const strongExisting = existingKeywords
  .filter(k => !keywordsToReplace.includes(k.keyword.toLowerCase()))
  .map(k => ({ ...k, is_sniper_seo: false }));

// 4. Pr√©parer les Snipers avec le flag (is_sniper_seo: true)
const preparedSnipers = sniperKeywords.map(k => ({
  ...k,
  is_sniper_seo: true
}));

// 5. Fusion et D√©doublonnage via Map
const finalMap = new Map();

// On remplit avec les 8 forts
strongExisting.forEach(k => finalMap.set(k.keyword.toLowerCase(), k));

// On ajoute les snipers (ils marquent ou ajoutent)
preparedSnipers.forEach(k => finalMap.set(k.keyword.toLowerCase(), k));

// 6. Reconstruction et compl√©tion √† 13
let finalListing = Array.from(finalMap.values());

// S√©curit√© : Si on a moins de 13 apr√®s fusion (doublons), on rep√™che dans les "faibles"
if (finalListing.length < 13) {
  for (const k of rankedExisting) {
    if (finalListing.length >= 13) break;
    const key = k.keyword.toLowerCase();
    if (!finalMap.has(key)) {
      finalListing.push({ ...k, is_sniper_seo: false });
      finalMap.set(key, k);
    }
  }
}

// Tri final par score d√©croissant pour l'affichage
finalListing.sort((a, b) => (b.opportunity_score || 0) - (a.opportunity_score || 0));

return [{
  json: {
    listing_seo_stats: finalListing,
    tag_list: finalListing.map(k => k.keyword),
    sniper_count: finalListing.filter(k => k.is_sniper_seo).length
  }
}];

