      const isEvergreen = monthlyVolumes.every(v => v > (avgVolume * 0.2));
      const isPromising = avgVolume > 200 && comp < 0.4;

    // --- √âTAPE 1 : FORMULE DE BASE (L'OPPORTUNIT√â STATISTIQUE) ---
    // Utilisation de la racine carr√©e pour aplatir les volumes extr√™mes
    let finalScore = (Math.sqrt(avgVolume) * (1 - comp + 0.2));

    // --- √âTAPE 2 : BOOST TRANSACTIONNEL ---
    // Un score de 10 double le score de base (+100%)
    finalScore *= (1 + (txScore / 10));


