        // P√©nalit√© s√©v√®re pour les hors-sujets (ex: Gender Reveal pour un tshirt Mama)
        finalScore *= 0.5;
    } 
    // Entre 3 et 6 : On ne booste ni ne p√©nalise. Le Volume et le TX Score d√©cident.



    // --- √âTAPE 4 : BOOSTS STRAT√âGIQUES ---
    // Priorit√© au Titre (+25%)
    if (intentLabel === 'Title') {
        finalScore *= 1.25;
    }

    // Boost Longue Tra√Æne (+20% pour 3 mots ou plus)
    const wordCount = kw.split(' ').length;
    if (wordCount >= 3) finalScore *= 1.2;

      // Boosts de Performance (Dataforseo trends)
      if (isTrending) finalScore *= 1.3;
      if (isPromising) finalScore *= 1.2;


    return {
        ...item,
        opportunity_score: Math.round(finalScore),
